mapper.select:
select u.id uid, u.username,u.avatar,f.id fid,f.name fname
from m_user u ,m_faculty f
where u.faculty_id =f.id and u.username = #{username}

mapper.update:
update m_user
username =#{username},
password= #{password},
avatar = #{avatar},
role_id = #{role.id}, faculty_id = #{faculty.id}
where id =#{id}

